package lec1;

public class maxof3number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=2;
		int b=5;
		int c=4;
		if(a > b && a > c) {
			System.out.println("a is greatest");
		}else if(b>c && b>a){
			System.out.println("b is greatest");
		}else {
			System.out.println("c is greatest");
		}

	}

}
